SELECT MIN(wordfreq.freq), chunk.text, source.name FROM chunk JOIN chunk_chunkfeat on chunk_chunkfeat.chunk_id = chunk.id AND chunk.id NOT IN (select c.id from chunk as c join chunk_chunkfeat as ccf on c.id = ccf.chunk_id  join chunkfeat as cf on cf.id = ccf.chunkfeat_id and cf.name = 'digit') JOIN wordfreq ON wordfreq.chunkfeat_id = chunk_chunkfeat.chunkfeat_id JOIN source_chunk on source_chunk.chunk_id = chunk.id JOIN source ON source_chunk.source_id = source.id GROUP BY chunk.id ORDER BY wordfreq.freq DESC LIMIT 40;
