SELECT MIN(wordfreq.freq), chunk.text, source.name, (select cf.name from chunkfeat as cf, chunk_chunkfeat as ccf where cf.id = ccf.chunkfeat_id and ccf.chunk_id = chunk.id and cf.name = 'digit') cfn FROM chunk JOIN chunk_chunkfeat on chunk_chunkfeat.chunk_id = chunk.id JOIN wordfreq ON wordfreq.chunkfeat_id = chunk_chunkfeat.chunkfeat_id JOIN source_chunk on source_chunk.chunk_id = chunk.id JOIN source ON source_chunk.source_id = source.id GROUP BY chunk.id HAVING cfn IS NULL ORDER BY wordfreq.freq DESC LIMIT 50;
